import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import "../App.css";
import { SlMicrophone } from "react-icons/sl";
import { pipeline } from '@xenova/transformers';

export default function Translate() {
  const [playing, setPlaying] = useState(false);
  const mediaRecorder = useRef(null);
  const [audioChunks, setAudioChunks] = useState([]);
  const [audio, setAudio] = useState(null);
  const mimeType = "audio/webm";
  const [transcribedText, setTranscribedText] = useState("");



  const getMicrophonePermission = async () => {
    if ("MediaRecorder" in window) {
      try {
        const streamData = await navigator.mediaDevices.getUserMedia({
          audio: true,
        });
        return streamData;
      } catch (err) {
        alert(err.message);
      }
    } else {
      alert("The MediaRecorder API is not supported in your browser.");
    }
  };

  // const startRecording = async () => {
  //   const stream = await getMicrophonePermission();
  //   setPlaying(true);
  //   const media = new MediaRecorder(stream, { type: mimeType });
  //   mediaRecorder.current = media;
  //   const localAudioChunks = [];
  //   mediaRecorder.current.ondataavailable = (event) => {
  //     if (typeof event.data === "undefined") return;
  //     if (event.data.size === 0) return;
  //     localAudioChunks.push(event.data);
  //     setAudioChunks(localAudioChunks);
  //   };
  //   mediaRecorder.current.start(5000);
  // };

  // const stopRecording = () => {
  //   setPlaying(false);
  //   mediaRecorder.current.stop();
  //   mediaRecorder.current.onstop = () => {
  //     const audioBlob = new Blob(audioChunks, { type: mimeType });
  //     const audioUrl = URL.createObjectURL(audioBlob);
  //     setAudio(audioUrl);

  //     const reader = new FileReader();
  //     reader.onloadend = () => {
  //       const audioDataUrl = reader.result;
  //       transcribe(audioDataUrl);
  //       setAudioChunks([]);
  //     };
  //     reader.readAsDataURL(audioBlob);
  //   };
  // };

  const startRecording = async () => {
    const stream = await getMicrophonePermission();
    setPlaying(true);
    const media = new MediaRecorder(stream, { type: mimeType });
    mediaRecorder.current = media;
    const localAudioChunks = [];
    mediaRecorder.current.ondataavailable = (event) => {
      if (typeof event.data === "undefined") return;
      if (event.data.size === 0) return;
      localAudioChunks.push(event.data);
      setAudioChunks(localAudioChunks);
    };
    mediaRecorder.current.start(5000);
  };

  const stopRecording = () => {
    setPlaying(false);
    mediaRecorder.current.stop();
    mediaRecorder.current.onstop = () => {
      const audioBlob = new Blob(audioChunks, { type: mimeType });
      const audioUrl = URL.createObjectURL(audioBlob); // Create the audio URL
      setAudio(audioUrl);

      const reader = new FileReader();
      reader.onloadend = () => {
        const audioDataUrl = reader.result;
        transcribe(audioUrl); // Pass the audio URL here
        setAudioChunks([]);
      };
      reader.readAsDataURL(audioBlob);
    };
  };

  const transcribe = async (audioUrl) => {
    try {
      const response = await fetch("<API_URL>", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ fileUrl: audioUrl, lang: "english" }),
      });

      if (!response.ok) {
        throw new Error("Failed to transcribe audio");
      }

      const result = await response.json();
      const transcribedResult = { text: result.text };
      setTranscribedText(transcribedResult.text);
    } catch (error) {
      console.log(error);
    }
  };


  useEffect(() => {
    if (audioChunks.length > 0) {
      const audioBlob = new Blob(audioChunks, { type: mimeType });
      const audioUrl = URL.createObjectURL(audioBlob);
      setAudio(audioUrl);
    }
  }, [audioChunks]);

  return (
    <div className="translate-audio">
      <div className="translate-overlay">
        <div className="translated-words">
          {/* Display the transcribed text here */}
          <p>{transcribedText}</p>
        </div>
        {audio && <audio src={audio} controls />}
        <div className="logic-buttons">
          <Link to="/">
            <button className="logic-btn-orange back-btn">back</button>
          </Link>
          <button
            className="logic-btn-orange"
            onClick={playing ? stopRecording : startRecording}
          >
            <SlMicrophone />
            {playing ? "Stop" : "Start"}
          </button>
        </div>
      </div>
    </div>
  );
}
