import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import "../App.css";
import { SlMicrophone } from "react-icons/sl";

export default function Transcribe(props) {
  const [recording, setRecording] = useState(false);
  const mediaRecorder = useRef(null);
  const [audioChunks, setAudioChunks] = useState([]);
  const mimeType = "audio/webm";
  const { message } = props.message;
  const [transcription, setTranscription] = useState("");

  const transcribe = async (audioData) => {
    try {
      const formData = new FormData();
      formData.append("audio", audioData);

      const response = await fetch("http://localhost:8000/transcribe", {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
      setTranscription(data.text);
    } catch (error) {
      console.error(error);
      setTranscription("An error occurred during transcription.");
    }
  };

  const getMicrophonePermission = async () => {
    if ("MediaRecorder" in window) {
      try {
        const streamData = await navigator.mediaDevices.getUserMedia({
          audio: true,
        });
        return new Promise((resolve, reject) => {
          resolve(streamData);
        });
      } catch (err) {
        alert(err.message);
      }
    } else {
      alert("The MediaRecorder API is not supported in your browser.");
    }
  };

  const startRecording = async () => {
    let stream = await getMicrophonePermission();
    setRecording(true);

    const media = new MediaRecorder(stream, { type: mimeType });
    mediaRecorder.current = media;
    mediaRecorder.current.start(5000);
    let localAudioChunks = [];
    mediaRecorder.current.ondataavailable = (event) => {
      if (typeof event.data === "undefined") return;
      if (event.data.size === 0) return;
      localAudioChunks.push(event.data);
      setAudioChunks(localAudioChunks);
    };
    setAudioChunks(localAudioChunks);
  };

  const stopRecording = () => {
    setRecording(false);
    mediaRecorder.current.stop();

    mediaRecorder.current.onstop = () => {
      const audioBlob = new Blob(audioChunks, { type: mimeType });
      transcribe(audioBlob);
      setAudioChunks([]);
    };
  };

  useEffect(() => {
    if (audioChunks.length > 0) {
      const audioBlob = new Blob(audioChunks, { type: mimeType });
      setAudioChunks([]);
    }
  }, [audioChunks]);

  return (
    <div className="translate-audio">
      <div className="translate-overlay">
        <div className="translated-words">
          <p>hello word, this is the translated words</p>
          {transcription && (
            <div className="transcription-result">
              <p>{transcription}</p>
            </div>
          )}
        </div>
        <div className="logic-buttons">
          <Link to="/">
            <button className="logic-btn-orange back-btn">back</button>
          </Link>
          <button
            className="logic-btn-orange"
            onClick={recording ? stopRecording : startRecording}
          >
            <SlMicrophone />
            {recording ? "Stop" : "Start"}{" "}
          </button>
        </div>
      </div>
    </div>
  );
}
